import { useQuery } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import { CommonErrorType } from '../@types/data-contracts'
import {
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { BannerApi } from './Banner.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const bannerApi = new BannerApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_BANNER_API = {
  LIST: (variables?: Parameter<typeof bannerApi.bannerList>) =>
    ['BANNER_LIST', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags banner
 * @name BannerList
 * @summary 배너 목록 조회
 * @request GET:/v1/banner/
 * @secure    */

export const useBannerListQuery = <
  TData = RequestFnReturn<typeof bannerApi.bannerList>,
>(
  params?: QueryHookParams<
    typeof bannerApi.bannerList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_BANNER_API.LIST(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => bannerApi.bannerList(params?.variables),
    ...params?.options,
  })
}
