/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const boardLikeApi = new BoardLikeApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_BOARD_LIKE_API = {
  TOGGLE_CREATE: () => ['BOARD_LIKE_TOGGLE_CREATE'],
}

/**
 * No description
 *
 * @tags board_like
 * @name BoardLikeToggleCreate
 * @summary 자유 게시글 좋아요 토글
 * @request POST:/v1/board/{board_id}/like/toggle/
 * @secure  */

export const useBoardLikeToggleCreateMutation = (
  params: MutationHookParams<
    typeof boardLikeApi.boardLikeToggleCreate,
    { error: BoardLikeToggleErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_BOARD_LIKE_API.TOGGLE_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: boardLikeApi.boardLikeToggleCreate,
    ...params?.options,
  })
}
