import { HttpClient, RequestParams } from '../@http-client'
import { CommonErrorType, ContactType } from '../@types/data-contracts'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class ContactApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags contact
   * @name ContactRetrieve
   * @summary Contact 상세 조회
   * @request GET:/v1/contact/{me}/
   * @secure
   */

  contactRetrieve = (variables: { me: string; params?: RequestParams }) =>
    this.request<ContactType, CommonErrorType>({
      path: `/v1/contact/${variables.me}/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables.params,
    })
}

