export class DepartmentApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags department
   * @name DepartmentList
   * @summary 분과 목록 조회
   * @request GET:/v1/department/
   * @secure
   */

  departmentList = (variables?: { params?: RequestParams }) =>
    this.request<DepartmentType[], CommonErrorType>({
      path: `/v1/department/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables?.params,
    })
}

