/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const departmentBoardApi = new DepartmentBoardApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_DEPARTMENT_BOARD_API = {
  LIST: (
    variables?: Parameter<typeof departmentBoardApi.departmentBoardList>,
  ) => ['DEPARTMENT_BOARD_LIST', variables].filter(isDefined),
  LIST_INFINITE: (
    variables?: Parameter<typeof departmentBoardApi.departmentBoardList>,
  ) => ['DEPARTMENT_BOARD_LIST_INFINITE', variables].filter(isDefined),
  CREATE: () => ['DEPARTMENT_BOARD_CREATE'],
  RETRIEVE: (
    variables?: Parameter<typeof departmentBoardApi.departmentBoardRetrieve>,
  ) => ['DEPARTMENT_BOARD_RETRIEVE', variables].filter(isDefined),
  UPDATE: () => ['DEPARTMENT_BOARD_UPDATE'],
  DESTROY: () => ['DEPARTMENT_BOARD_DESTROY'],
}

/**
 * No description
 *
 * @tags department_board
 * @name DepartmentBoardList
 * @summary 분과 게시글 목록 조회
 * @request GET:/v1/department_board/
 * @secure    */

export const useDepartmentBoardListQuery = <
  TData = RequestFnReturn<typeof departmentBoardApi.departmentBoardList>,
>(
  params: QueryHookParams<
    typeof departmentBoardApi.departmentBoardList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_DEPARTMENT_BOARD_API.LIST(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => departmentBoardApi.departmentBoardList(params.variables),
    ...params?.options,
  })
}

/**
 * No description    *      * @tags department_board
 * @name DepartmentBoardList
 * @summary 분과 게시글 목록 조회
 * @request GET:/v1/department_board/
 * @secure    */
export const useDepartmentBoardListInfiniteQuery = <
  TData = InfiniteData<
    RequestFnReturn<typeof departmentBoardApi.departmentBoardList>,
    Parameter<typeof departmentBoardApi.departmentBoardList>
  >,
>(
  params: InfiniteQueryHookParams<
    typeof departmentBoardApi.departmentBoardList,
    CommonErrorType,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_DEPARTMENT_BOARD_API.LIST_INFINITE(
    params.variables,
  )
  return useInfiniteQuery({
    queryKey,
    initialPageParam: null,
    queryFn: ({ pageParam }) => {
      const offset = pageParam ?? params?.variables?.query?.offset ?? 0
      return departmentBoardApi.departmentBoardList({
        ...params?.variables,
        query: { ...params?.variables?.query, offset },
      })
    },
    getNextPageParam: (lastPage, allPages) => {
      if (!lastPage?.isNext) return null
      const fetchedLength = allPages?.length || 0
      const initialOffset = params?.variables?.query?.offset || 0
      const limit = params?.variables?.query?.limit || 0

      return initialOffset + fetchedLength * limit
    },
    ...params?.options,
  })
}

