/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const departmentBoardCommentApi = new DepartmentBoardCommentApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_DEPARTMENT_BOARD_COMMENT_API = {
  LIST: (
    variables?: Parameter<
      typeof departmentBoardCommentApi.departmentBoardCommentList
    >,
  ) => ['DEPARTMENT_BOARD_COMMENT_LIST', variables].filter(isDefined),
  LIST_INFINITE: (
    variables?: Parameter<
      typeof departmentBoardCommentApi.departmentBoardCommentList
    >,
  ) => ['DEPARTMENT_BOARD_COMMENT_LIST_INFINITE', variables].filter(isDefined),
  CREATE: () => ['DEPARTMENT_BOARD_COMMENT_CREATE'],
  UPDATE: () => ['DEPARTMENT_BOARD_COMMENT_UPDATE'],
  DESTROY: () => ['DEPARTMENT_BOARD_COMMENT_DESTROY'],
}

/**
 * No description
 *
 * @tags department_board_comment
 * @name DepartmentBoardCommentList
 * @summary 분과 게시글 댓글 목록 조회
 * @request GET:/v1/department_board/{department_board_id}/comment/
 * @secure    */

export const useDepartmentBoardCommentListQuery = <
  TData = RequestFnReturn<
    typeof departmentBoardCommentApi.departmentBoardCommentList
  >,
>(
  params: QueryHookParams<
    typeof departmentBoardCommentApi.departmentBoardCommentList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_DEPARTMENT_BOARD_COMMENT_API.LIST(params.variables)
  return useQuery({
    queryKey,
    queryFn: () =>
      departmentBoardCommentApi.departmentBoardCommentList(params.variables),
    ...params?.options,
  })
}

/**
 * No description    *      * @tags department_board_comment
 * @name DepartmentBoardCommentList
 * @summary 분과 게시글 댓글 목록 조회
 * @request GET:/v1/department_board/{department_board_id}/comment/
 * @secure    */
export const useDepartmentBoardCommentListInfiniteQuery = <
  TData = InfiniteData<
    RequestFnReturn<
      typeof departmentBoardCommentApi.departmentBoardCommentList
    >,
    Parameter<typeof departmentBoardCommentApi.departmentBoardCommentList>
  >,
>(
  params: InfiniteQueryHookParams<
    typeof departmentBoardCommentApi.departmentBoardCommentList,
    CommonErrorType,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_DEPARTMENT_BOARD_COMMENT_API.LIST_INFINITE(
    params.variables,
  )
  return useInfiniteQuery({
    queryKey,
    initialPageParam: null,
    queryFn: ({ pageParam }: { pageParam: any }) => {
      return departmentBoardCommentApi.departmentBoardCommentList({
        ...params?.variables,
        query:
          pageParam ?
            { ...params?.variables?.query, cursor: pageParam }
          : { ...params?.variables?.query },
      })
    },
    getNextPageParam: (lastPage) => {
      return lastPage.cursor
    },
    ...params?.options,
  })
}

