import { HttpClient, RequestParams } from '../@http-client'
import {
  CommonErrorType,
  DocumentType,
  PaginatedDocumentListType,
} from '../@types/data-contracts'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class DocumentApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags document
   * @name DocumentList
   * @summary 자료 목록 조회
   * @request GET:/v1/document/
   * @secure
   */

  documentList = (variables?: {
    query?: {
      title?: string
      /** 페이지당 반환할 결과 수. */
      limit?: number
      /** 결과를 반환할 초기 인덱스. */
      offset?: number
    }
    params?: RequestParams
  }) =>
    this.request<PaginatedDocumentListType, CommonErrorType>({
      path: `/v1/document/`,
      method: 'GET',
      query: variables?.query,
      secure: true,
      format: 'json',
      ...variables?.params,
    }) /**
   * No description
   *
   * @tags document
   * @name DocumentRetrieve
   * @summary 자료 상세 조회
   * @request GET:/v1/document/{id}/
   * @secure
   */

  documentRetrieve = (variables: { id: number; params?: RequestParams }) =>
    this.request<DocumentType, CommonErrorType>({
      path: `/v1/document/${variables.id}/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables.params,
    })
}

