import { useMutation } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import {
  CommonErrorType,
  EmailVerifierConfirmErrorMessageType,
  EmailVerifierCreateErrorMessageType,
} from '../@types/data-contracts'
import { MutationHookParams } from '../@types/react-query-type'
import { EmailVerifierApi } from './EmailVerifier.api'

export const emailVerifierApi = new EmailVerifierApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */

export const QUERY_KEY_EMAIL_VERIFIER_API = {
  CREATE: () => ['EMAIL_VERIFIER_CREATE'],
  CONFIRM_CREATE: () => ['EMAIL_VERIFIER_CONFIRM_CREATE'],
}

/**
 * No description
 *
 * @tags email_verifier
 * @name EmailVerifierCreate
 * @summary 이메일 검증 등록
 * @request POST:/v1/email_verifier/
 * @secure  */

export const useEmailVerifierCreateMutation = (
  params: MutationHookParams<
    typeof emailVerifierApi.emailVerifierCreate,
    { error: EmailVerifierCreateErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_EMAIL_VERIFIER_API.CREATE()
  return useMutation({
    mutationKey,
    mutationFn: emailVerifierApi.emailVerifierCreate,
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags email_verifier
 * @name EmailVerifierConfirmCreate
 * @summary 이메일 검증 확인
 * @request POST:/v1/email_verifier/confirm/
 * @secure  */

export const useEmailVerifierConfirmCreateMutation = (
  params: MutationHookParams<
    typeof emailVerifierApi.emailVerifierConfirmCreate,
    { error: EmailVerifierConfirmErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_EMAIL_VERIFIER_API.CONFIRM_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: emailVerifierApi.emailVerifierConfirmCreate,
    ...params?.options,
  })
}

