import { HttpClient, RequestParams } from '../@http-client'
import {
  CommonErrorType,
  EventType,
  PaginatedEventListType,
} from '../@types/data-contracts'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class EventApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags event
   * @name EventList
   * @summary 행사 목록 조회
   * @request GET:/v1/event/
   * @secure
   */

  eventList = (variables?: {
    query?: {
      /** 페이지당 반환할 결과 수. */
      limit?: number
      /** 결과를 반환할 초기 인덱스. */
      offset?: number
    }
    params?: RequestParams
  }) =>
    this.request<PaginatedEventListType, CommonErrorType>({
      path: `/v1/event/`,
      method: 'GET',
      query: variables?.query,
      secure: true,
      format: 'json',
      ...variables?.params,
    }) /**
   * No description
   *
   * @tags event
   * @name EventRetrieve
   * @summary 행사 상세 조회
   * @request GET:/v1/event/{id}/
   * @secure
   */

  eventRetrieve = (variables: { id: number; params?: RequestParams }) =>
    this.request<EventType, CommonErrorType>({
      path: `/v1/event/${variables.id}/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables.params,
    })
}

