import { InfiniteData, useInfiniteQuery, useQuery } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import { CommonErrorType } from '../@types/data-contracts'
import {
  InfiniteQueryHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { EventApi } from './Event.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const eventApi = new EventApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_EVENT_API = {
  LIST: (variables?: Parameter<typeof eventApi.eventList>) =>
    ['EVENT_LIST', variables].filter(isDefined),
  LIST_INFINITE: (variables?: Parameter<typeof eventApi.eventList>) =>
    ['EVENT_LIST_INFINITE', variables].filter(isDefined),
  RETRIEVE: (variables?: Parameter<typeof eventApi.eventRetrieve>) =>
    ['EVENT_RETRIEVE', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags event
 * @name EventList
 * @summary 행사 목록 조회
 * @request GET:/v1/event/
 * @secure    */

export const useEventListQuery = <
  TData = RequestFnReturn<typeof eventApi.eventList>,
>(
  params?: QueryHookParams<
    typeof eventApi.eventList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_EVENT_API.LIST(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => eventApi.eventList(params?.variables),
    ...params?.options,
  })
}

/**
 * No description    *      * @tags event
 * @name EventList
 * @summary 행사 목록 조회
 * @request GET:/v1/event/
 * @secure    */
export const useEventListInfiniteQuery = <
  TData = InfiniteData<
    RequestFnReturn<typeof eventApi.eventList>,
    Parameter<typeof eventApi.eventList>
  >,
>(
  params?: InfiniteQueryHookParams<
    typeof eventApi.eventList,
    CommonErrorType,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_EVENT_API.LIST_INFINITE(params?.variables)
  return useInfiniteQuery({
    queryKey,
    initialPageParam: null,
    queryFn: ({ pageParam }) => {
      const offset = pageParam ?? params?.variables?.query?.offset ?? 0
      return eventApi.eventList({
        ...params?.variables,
        query: { ...params?.variables?.query, offset },
      })
    },
    getNextPageParam: (lastPage, allPages) => {
      if (!lastPage?.isNext) return null
      const fetchedLength = allPages?.length || 0
      const initialOffset = params?.variables?.query?.offset || 0
      const limit = params?.variables?.query?.limit || 0

      return initialOffset + fetchedLength * limit
    },
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags event
 * @name EventRetrieve
 * @summary 행사 상세 조회
 * @request GET:/v1/event/{id}/
 * @secure    */

export const useEventRetrieveQuery = <
  TData = RequestFnReturn<typeof eventApi.eventRetrieve>,
>(
  params: QueryHookParams<
    typeof eventApi.eventRetrieve,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_EVENT_API.RETRIEVE(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => eventApi.eventRetrieve(params.variables),
    ...params?.options,
  })
}
