export const useLiturgyFlowerCommentCreateMutation = (
  params: MutationHookParams<
    typeof liturgyFlowerCommentApi.liturgyFlowerCommentCreate,
    { error: LiturgyFlowerCommentErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_LITURGY_FLOWER_COMMENT_API.CREATE()
  return useMutation({
    mutationKey,
    mutationFn: liturgyFlowerCommentApi.liturgyFlowerCommentCreate,
    ...params?.options,
  })
}

