/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const liturgyFlowerLikeApi = new LiturgyFlowerLikeApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_LITURGY_FLOWER_LIKE_API = {
  TOGGLE_CREATE: () => ['LITURGY_FLOWER_LIKE_TOGGLE_CREATE'],
}

/**
 * No description
 *
 * @tags liturgy_flower_like
 * @name LiturgyFlowerLikeToggleCreate
 * @summary 전례꽃 좋아요 토글
 * @request POST:/v1/liturgy_flower/{liturgy_flower_id}/like/toggle/
 * @secure  */

export const useLiturgyFlowerLikeToggleCreateMutation = (
  params: MutationHookParams<
    typeof liturgyFlowerLikeApi.liturgyFlowerLikeToggleCreate,
    { error: LiturgyFlowerLikeToggleErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_LITURGY_FLOWER_LIKE_API.TOGGLE_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: liturgyFlowerLikeApi.liturgyFlowerLikeToggleCreate,
    ...params?.options,
  })
}
