import { ContentType, HttpClient, RequestParams } from '../@http-client'
import {
  CommonErrorType,
  NewsErrorMessageType,
  NewsRequestType,
  NewsType,
  PaginatedNewsListType,
} from '../@types/data-contracts'
import { DeepOmitReadOnly } from '../@types/util-types'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class NewsApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags news
   * @name NewsList
   * @summary News 목록 조회
   * @request GET:/v1/news/
   * @secure
   */

  newsList = (variables?: {
    query?: {
      /** 페이지당 반환할 결과 수. */
      limit?: number
      /** 결과를 반환할 초기 인덱스. */
      offset?: number
    }
    params?: RequestParams
  }) =>
    this.request<PaginatedNewsListType, CommonErrorType>({
      path: `/v1/news/`,
      method: 'GET',
      query: variables?.query,
      secure: true,
      format: 'json',
      ...variables?.params,
    }) /**
   * No description
   *
   * @tags news
   * @name NewsCreate
   * @summary News 등록
   * @request POST:/v1/news/
   * @secure
   */

  newsCreate = (variables: {
    data: DeepOmitReadOnly<NewsRequestType>
    params?: RequestParams
  }) =>
    this.request<NewsType, NewsErrorMessageType | CommonErrorType>({
      path: `/v1/news/`,
      method: 'POST',
      body: variables.data,
      secure: true,
      type: ContentType.Json,
      format: 'json',
      ...variables.params,
    }) /**
   * No description
   *
   * @tags news
   * @name NewsRetrieve
   * @summary News 상세 조회
   * @request GET:/v1/news/{id}/
   * @secure
   */

  newsRetrieve = (variables: { id: number; params?: RequestParams }) =>
    this.request<NewsType, CommonErrorType>({
      path: `/v1/news/${variables.id}/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables.params,
    }) /**
   * No description
   *
   * @tags news
   * @name NewsUpdate
   * @summary News 수정
   * @request PUT:/v1/news/{id}/
   * @secure
   */

  newsUpdate = (variables: {
    id: number
    data: DeepOmitReadOnly<NewsRequestType>
    params?: RequestParams
  }) =>
    this.request<NewsType, NewsErrorMessageType | CommonErrorType>({
      path: `/v1/news/${variables.id}/`,
      method: 'PUT',
      body: variables.data,
      secure: true,
      type: ContentType.Json,
      format: 'json',
      ...variables.params,
    }) /**
   * No description
   *
   * @tags news
   * @name NewsDestroy
   * @summary News 삭제
   * @request DELETE:/v1/news/{id}/
   * @secure
   */

  newsDestroy = (variables: { id: number; params?: RequestParams }) =>
    this.request<void, CommonErrorType>({
      path: `/v1/news/${variables.id}/`,
      method: 'DELETE',
      secure: true,
      ...variables.params,
    })
}

