import {
  InfiniteData,
  useInfiniteQuery,
  useMutation,
  useQuery,
} from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import { CommonErrorType, NewsErrorMessageType } from '../@types/data-contracts'
import {
  InfiniteQueryHookParams,
  MutationHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { NewsApi } from './News.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const newsApi = new NewsApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_NEWS_API = {
  LIST: (variables?: Parameter<typeof newsApi.newsList>) =>
    ['NEWS_LIST', variables].filter(isDefined),
  LIST_INFINITE: (variables?: Parameter<typeof newsApi.newsList>) =>
    ['NEWS_LIST_INFINITE', variables].filter(isDefined),
  CREATE: () => ['NEWS_CREATE'],
  LATEST_RETRIEVE: (variables?: Parameter<typeof newsApi.newsLatestRetrieve>) =>
    ['NEWS_LATEST_RETRIEVE', variables].filter(isDefined),
  RETRIEVE: (variables?: Parameter<typeof newsApi.newsRetrieve>) =>
    ['NEWS_RETRIEVE', variables].filter(isDefined),
  UPDATE: () => ['NEWS_UPDATE'],
  DESTROY: () => ['NEWS_DESTROY'],
}

/**
 * No description
 *
 * @tags news
 * @name NewsList
 * @summary News 목록 조회
 * @request GET:/v1/news/
 * @secure    */

export const useNewsListQuery = <
  TData = RequestFnReturn<typeof newsApi.newsList>,
>(
  params?: QueryHookParams<
    typeof newsApi.newsList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_NEWS_API.LIST(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => newsApi.newsList(params?.variables),
    ...params?.options,
  })
}

/**
 * No description    *      * @tags news
 * @name NewsList
 * @summary News 목록 조회
 * @request GET:/v1/news/
 * @secure    */
export const useNewsListInfiniteQuery = <
  TData = InfiniteData<
    RequestFnReturn<typeof newsApi.newsList>,
    Parameter<typeof newsApi.newsList>
  >,
>(
  params?: InfiniteQueryHookParams<
    typeof newsApi.newsList,
    CommonErrorType,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_NEWS_API.LIST_INFINITE(params?.variables)
  return useInfiniteQuery({
    queryKey,
    initialPageParam: null,
    queryFn: ({ pageParam }) => {
      const offset = pageParam ?? params?.variables?.query?.offset ?? 0
      return newsApi.newsList({
        ...params?.variables,
        query: { ...params?.variables?.query, offset },
      })
    },
    getNextPageParam: (lastPage, allPages) => {
      if (!lastPage?.isNext) return null
      const fetchedLength = allPages?.length || 0
      const initialOffset = params?.variables?.query?.offset || 0
      const limit = params?.variables?.query?.limit || 0

      return initialOffset + fetchedLength * limit
    },
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags news
 * @name NewsCreate
 * @summary News 등록
 * @request POST:/v1/news/
 * @secure  */

export const useNewsCreateMutation = (
  params: MutationHookParams<
    typeof newsApi.newsCreate,
    { error: NewsErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_NEWS_API.CREATE()
  return useMutation({
    mutationKey,
    mutationFn: newsApi.newsCreate,
    ...params?.options,
  })
}

