import { InfiniteData, useInfiniteQuery, useQuery } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import { CommonErrorType } from '../@types/data-contracts'
import {
  InfiniteQueryHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { NoticeApi } from './Notice.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const noticeApi = new NoticeApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_NOTICE_API = {
  LIST: (variables?: Parameter<typeof noticeApi.noticeList>) =>
    ['NOTICE_LIST', variables].filter(isDefined),
  LIST_INFINITE: (variables?: Parameter<typeof noticeApi.noticeList>) =>
    ['NOTICE_LIST_INFINITE', variables].filter(isDefined),
  RETRIEVE: (variables?: Parameter<typeof noticeApi.noticeRetrieve>) =>
    ['NOTICE_RETRIEVE', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags notice
 * @name NoticeList
 * @summary 공지사항 목록 조회
 * @request GET:/v1/notice/
 * @secure    */

export const useNoticeListQuery = <
  TData = RequestFnReturn<typeof noticeApi.noticeList>,
>(
  params?: QueryHookParams<
    typeof noticeApi.noticeList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_NOTICE_API.LIST(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => noticeApi.noticeList(params?.variables),
    ...params?.options,
  })
}

/**
 * No description    *      * @tags notice
 * @name NoticeList
 * @summary 공지사항 목록 조회
 * @request GET:/v1/notice/
 * @secure    */
export const useNoticeListInfiniteQuery = <
  TData = InfiniteData<
    RequestFnReturn<typeof noticeApi.noticeList>,
    Parameter<typeof noticeApi.noticeList>
  >,
>(
  params?: InfiniteQueryHookParams<
    typeof noticeApi.noticeList,
    CommonErrorType,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_NOTICE_API.LIST_INFINITE(params?.variables)
  return useInfiniteQuery({
    queryKey,
    initialPageParam: null,
    queryFn: ({ pageParam }) => {
      const offset = pageParam ?? params?.variables?.query?.offset ?? 0
      return noticeApi.noticeList({
        ...params?.variables,
        query: { ...params?.variables?.query, offset },
      })
    },
    getNextPageParam: (lastPage, allPages) => {
      if (!lastPage?.isNext) return null
      const fetchedLength = allPages?.length || 0
      const initialOffset = params?.variables?.query?.offset || 0
      const limit = params?.variables?.query?.limit || 0

      return initialOffset + fetchedLength * limit
    },
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags notice
 * @name NoticeRetrieve
 * @summary 공지사항 상세 조회
 * @request GET:/v1/notice/{id}/
 * @secure    */

export const useNoticeRetrieveQuery = <
  TData = RequestFnReturn<typeof noticeApi.noticeRetrieve>,
>(
  params: QueryHookParams<
    typeof noticeApi.noticeRetrieve,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_NOTICE_API.RETRIEVE(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => noticeApi.noticeRetrieve(params.variables),
    ...params?.options,
  })
}
