export const usePastorHistoryCreateMutation = (
  params: MutationHookParams<
    typeof pastorHistoryApi.pastorHistoryCreate,
    { error: PastorHistoryErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_PASTOR_HISTORY_API.CREATE()
  return useMutation({
    mutationKey,
    mutationFn: pastorHistoryApi.pastorHistoryCreate,
    ...params?.options,
  })
}

