import { useMutation, useQuery } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import {
  CommonErrorType,
  PastoralGuidelinesErrorMessageType,
} from '../@types/data-contracts'
import {
  MutationHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { PastoralGuidelinesApi } from './PastoralGuidelines.api'

export const pastoralGuidelinesApi = new PastoralGuidelinesApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */

export const QUERY_KEY_PASTORAL_GUIDELINES_API = {
  RETRIEVE: (
    variables?: Parameter<
      typeof pastoralGuidelinesApi.pastoralGuidelinesRetrieve
    >,
  ) => ['PASTORAL_GUIDELINES_RETRIEVE', variables].filter(isDefined),
  PARTIAL_UPDATE: () => ['PASTORAL_GUIDELINES_PARTIAL_UPDATE'],
}

/**
 * No description
 *
 * @tags pastoral_guidelines
 * @name PastoralGuidelinesRetrieve
 * @summary 사목지침 상세 조회
 * @request GET:/v1/pastoral_guidelines/{me}/
 * @secure    */

export const usePastoralGuidelinesRetrieveQuery = <
  TData = RequestFnReturn<
    typeof pastoralGuidelinesApi.pastoralGuidelinesRetrieve
  >,
>(
  params: QueryHookParams<
    typeof pastoralGuidelinesApi.pastoralGuidelinesRetrieve,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_PASTORAL_GUIDELINES_API.RETRIEVE(params.variables)
  return useQuery({
    queryKey,
    queryFn: () =>
      pastoralGuidelinesApi.pastoralGuidelinesRetrieve(params.variables),
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags pastoral_guidelines
 * @name PastoralGuidelinesPartialUpdate
 * @request PATCH:/v1/pastoral_guidelines/{me}/
 * @secure  */

export const usePastoralGuidelinesPartialUpdateMutation = (
  params: MutationHookParams<
    typeof pastoralGuidelinesApi.pastoralGuidelinesPartialUpdate,
    { error: PastoralGuidelinesErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_PASTORAL_GUIDELINES_API.PARTIAL_UPDATE()
  return useMutation({
    mutationKey,
    mutationFn: pastoralGuidelinesApi.pastoralGuidelinesPartialUpdate,
    ...params?.options,
  })
}

