/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const priestApi = new PriestApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_PRIEST_API = {
  LIST: (variables?: Parameter<typeof priestApi.priestList>) =>
    ['PRIEST_LIST', variables].filter(isDefined),
  LIST_INFINITE: (variables?: Parameter<typeof priestApi.priestList>) =>
    ['PRIEST_LIST_INFINITE', variables].filter(isDefined),
  CREATE: () => ['PRIEST_CREATE'],
  ASSOCIATE_HISTORY_RETRIEVE: (
    variables?: Parameter<typeof priestApi.priestAssociateHistoryRetrieve>,
  ) => ['PRIEST_ASSOCIATE_HISTORY_RETRIEVE', variables].filter(isDefined),
  HISTORY_RETRIEVE: (
    variables?: Parameter<typeof priestApi.priestHistoryRetrieve>,
  ) => ['PRIEST_HISTORY_RETRIEVE', variables].filter(isDefined),
  PASTOR_HISTORY_RETRIEVE: (
    variables?: Parameter<typeof priestApi.priestPastorHistoryRetrieve>,
  ) => ['PRIEST_PASTOR_HISTORY_RETRIEVE', variables].filter(isDefined),
  RETRIEVE: (variables?: Parameter<typeof priestApi.priestRetrieve>) =>
    ['PRIEST_RETRIEVE', variables].filter(isDefined),
  UPDATE: () => ['PRIEST_UPDATE'],
  DESTROY: () => ['PRIEST_DESTROY'],
}

/**
 * No description
 *
 * @tags priest
 * @name PriestList
 * @summary 사제 목록 조회 (재임 중)
 * @request GET:/v1/priest/
 * @secure    */

export const usePriestListQuery = <
  TData = RequestFnReturn<typeof priestApi.priestList>,
>(
  params?: QueryHookParams<
    typeof priestApi.priestList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_PRIEST_API.LIST(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => priestApi.priestList(params?.variables),
    ...params?.options,
  })
}

/**
 * No description    *      * @tags priest
 * @name PriestList
 * @summary 사제 목록 조회 (재임 중)
 * @request GET:/v1/priest/
 * @secure    */
export const usePriestListInfiniteQuery = <
  TData = InfiniteData<
    RequestFnReturn<typeof priestApi.priestList>,
    Parameter<typeof priestApi.priestList>
  >,
>(
  params?: InfiniteQueryHookParams<
    typeof priestApi.priestList,
    CommonErrorType,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_PRIEST_API.LIST_INFINITE(params?.variables)
  return useInfiniteQuery({
    queryKey,
    initialPageParam: null,
    queryFn: ({ pageParam }) => {
      const offset = pageParam ?? params?.variables?.query?.offset ?? 0
      return priestApi.priestList({
        ...params?.variables,
        query: { ...params?.variables?.query, offset },
      })
    },
    getNextPageParam: (lastPage, allPages) => {
      if (!lastPage?.isNext) return null
      const fetchedLength = allPages?.length || 0
      const initialOffset = params?.variables?.query?.offset || 0
      const limit = params?.variables?.query?.limit || 0

      return initialOffset + fetchedLength * limit
    },
    ...params?.options,
  })
}

