import { useMutation, useQuery } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import {
  CommonErrorType,
  PriestErrorMessageType,
} from '../@types/data-contracts'
import {
  MutationHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { PriestApi } from './Priest.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const priestApi = new PriestApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_PRIEST_API = {
  LIST: (variables?: Parameter<typeof priestApi.priestList>) =>
    ['PRIEST_LIST', variables].filter(isDefined),
  CREATE: () => ['PRIEST_CREATE'],
  RETRIEVE: (variables?: Parameter<typeof priestApi.priestRetrieve>) =>
    ['PRIEST_RETRIEVE', variables].filter(isDefined),
  UPDATE: () => ['PRIEST_UPDATE'],
  DESTROY: () => ['PRIEST_DESTROY'],
}

/**
 * No description
 *
 * @tags priest
 * @name PriestList
 * @summary 사제 목록 조회
 * @request GET:/v1/priest/
 * @secure    */

export const usePriestListQuery = <
  TData = RequestFnReturn<typeof priestApi.priestList>,
>(
  params?: QueryHookParams<
    typeof priestApi.priestList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_PRIEST_API.LIST(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => priestApi.priestList(params?.variables),
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags priest
 * @name PriestCreate
 * @summary 사제 등록
 * @request POST:/v1/priest/
 * @secure  */

export const usePriestCreateMutation = (
  params: MutationHookParams<
    typeof priestApi.priestCreate,
    { error: PriestErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_PRIEST_API.CREATE()
  return useMutation({
    mutationKey,
    mutationFn: priestApi.priestCreate,
    ...params?.options,
  })
}

