import {
  InfiniteData,
  useInfiniteQuery,
  useMutation,
  useQuery,
} from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import {
  CommonErrorType,
  PriestHistoryErrorMessageType,
} from '../@types/data-contracts'
import {
  InfiniteQueryHookParams,
  MutationHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { PriestHistoryApi } from './PriestHistory.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const priestHistoryApi = new PriestHistoryApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_PRIEST_HISTORY_API = {
  LIST: (variables?: Parameter<typeof priestHistoryApi.priestHistoryList>) =>
    ['PRIEST_HISTORY_LIST', variables].filter(isDefined),
  LIST_INFINITE: (
    variables?: Parameter<typeof priestHistoryApi.priestHistoryList>,
  ) => ['PRIEST_HISTORY_LIST_INFINITE', variables].filter(isDefined),
  CREATE: () => ['PRIEST_HISTORY_CREATE'],
  UPDATE: () => ['PRIEST_HISTORY_UPDATE'],
  DESTROY: () => ['PRIEST_HISTORY_DESTROY'],
}

/**
 * No description
 *
 * @tags priest_history
 * @name PriestHistoryList
 * @summary 본당 출신 사제 목록 조회
 * @request GET:/v1/priest_history/
 * @secure    */

export const usePriestHistoryListQuery = <
  TData = RequestFnReturn<typeof priestHistoryApi.priestHistoryList>,
>(
  params?: QueryHookParams<
    typeof priestHistoryApi.priestHistoryList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_PRIEST_HISTORY_API.LIST(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => priestHistoryApi.priestHistoryList(params?.variables),
    ...params?.options,
  })
}

/**
 * No description    *      * @tags priest_history
 * @name PriestHistoryList
 * @summary 본당 출신 사제 목록 조회
 * @request GET:/v1/priest_history/
 * @secure    */
export const usePriestHistoryListInfiniteQuery = <
  TData = InfiniteData<
    RequestFnReturn<typeof priestHistoryApi.priestHistoryList>,
    Parameter<typeof priestHistoryApi.priestHistoryList>
  >,
>(
  params?: InfiniteQueryHookParams<
    typeof priestHistoryApi.priestHistoryList,
    CommonErrorType,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_PRIEST_HISTORY_API.LIST_INFINITE(params?.variables)
  return useInfiniteQuery({
    queryKey,
    initialPageParam: null,
    queryFn: ({ pageParam }) => {
      const offset = pageParam ?? params?.variables?.query?.offset ?? 0
      return priestHistoryApi.priestHistoryList({
        ...params?.variables,
        query: { ...params?.variables?.query, offset },
      })
    },
    getNextPageParam: (lastPage, allPages) => {
      if (!lastPage?.isNext) return null
      const fetchedLength = allPages?.length || 0
      const initialOffset = params?.variables?.query?.offset || 0
      const limit = params?.variables?.query?.limit || 0

      return initialOffset + fetchedLength * limit
    },
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags priest_history
 * @name PriestHistoryCreate
 * @summary 본당 출신 사제 등록
 * @request POST:/v1/priest_history/
 * @secure  */

export const usePriestHistoryCreateMutation = (
  params: MutationHookParams<
    typeof priestHistoryApi.priestHistoryCreate,
    { error: PriestHistoryErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_PRIEST_HISTORY_API.CREATE()
  return useMutation({
    mutationKey,
    mutationFn: priestHistoryApi.priestHistoryCreate,
    ...params?.options,
  })
}

