import { useMutation, useQuery } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import {
  CommonErrorType,
  ReligiousHistoryErrorMessageType,
} from '../@types/data-contracts'
import {
  MutationHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { ReligiousHistoryApi } from './ReligiousHistory.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const religiousHistoryApi = new ReligiousHistoryApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_RELIGIOUS_HISTORY_API = {
  LIST: (
    variables?: Parameter<typeof religiousHistoryApi.religiousHistoryList>,
  ) => ['RELIGIOUS_HISTORY_LIST', variables].filter(isDefined),
  CREATE: () => ['RELIGIOUS_HISTORY_CREATE'],
  UPDATE: () => ['RELIGIOUS_HISTORY_UPDATE'],
  DESTROY: () => ['RELIGIOUS_HISTORY_DESTROY'],
}

/**
 * No description
 *
 * @tags religious_history
 * @name ReligiousHistoryList
 * @summary 본당 출신 수도자 목록 조회
 * @request GET:/v1/religious_history/
 * @secure    */

export const useReligiousHistoryListQuery = <
  TData = RequestFnReturn<typeof religiousHistoryApi.religiousHistoryList>,
>(
  params?: QueryHookParams<
    typeof religiousHistoryApi.religiousHistoryList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_RELIGIOUS_HISTORY_API.LIST(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => religiousHistoryApi.religiousHistoryList(params?.variables),
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags religious_history
 * @name ReligiousHistoryCreate
 * @summary 본당 출신 수도자 등록
 * @request POST:/v1/religious_history/
 * @secure  */

export const useReligiousHistoryCreateMutation = (
  params: MutationHookParams<
    typeof religiousHistoryApi.religiousHistoryCreate,
    { error: ReligiousHistoryErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_RELIGIOUS_HISTORY_API.CREATE()
  return useMutation({
    mutationKey,
    mutationFn: religiousHistoryApi.religiousHistoryCreate,
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags religious_history
 * @name ReligiousHistoryUpdate
 * @summary 본당 출신 수도자 수정
 * @request PUT:/v1/religious_history/{id}/
 * @secure  */

export const useReligiousHistoryUpdateMutation = (
  params: MutationHookParams<
    typeof religiousHistoryApi.religiousHistoryUpdate,
    { error: ReligiousHistoryErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_RELIGIOUS_HISTORY_API.UPDATE()
  return useMutation({
    mutationKey,
    mutationFn: religiousHistoryApi.religiousHistoryUpdate,
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags religious_history
 * @name ReligiousHistoryDestroy
 * @summary 본당 출신 수도자 삭제
 * @request DELETE:/v1/religious_history/{id}/
 * @secure  */

export const useReligiousHistoryDestroyMutation = (
  params: MutationHookParams<
    typeof religiousHistoryApi.religiousHistoryDestroy,
    { error: CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_RELIGIOUS_HISTORY_API.DESTROY()
  return useMutation({
    mutationKey,
    mutationFn: religiousHistoryApi.religiousHistoryDestroy,
    ...params?.options,
  })
}
