import { HttpClient, RequestParams } from '../@http-client'
import { CommonErrorType, ScheduleType } from '../@types/data-contracts'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class ScheduleApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags schedule
   * @name ScheduleList
   * @summary 일정 목록 조회
   * @request GET:/v1/schedule/
   * @secure
   */

  scheduleList = (variables: {
    query: {
      /** 연도 */
      year: string
      /** 월 */
      month: string
    }
    params?: RequestParams
  }) =>
    this.request<ScheduleType[], CommonErrorType>({
      path: `/v1/schedule/`,
      method: 'GET',
      query: variables.query,
      secure: true,
      format: 'json',
      ...variables.params,
    })
}

