import { useQuery } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import { CommonErrorType } from '../@types/data-contracts'
import {
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { ScheduleApi } from './Schedule.api'

export const scheduleApi = new ScheduleApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */

export const QUERY_KEY_SCHEDULE_API = {
  LIST: (variables?: Parameter<typeof scheduleApi.scheduleList>) =>
    ['SCHEDULE_LIST', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags schedule
 * @name ScheduleList
 * @summary 일정 목록 조회
 * @request GET:/v1/schedule/
 * @secure    */

export const useScheduleListQuery = <
  TData = RequestFnReturn<typeof scheduleApi.scheduleList>,
>(
  params: QueryHookParams<
    typeof scheduleApi.scheduleList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_SCHEDULE_API.LIST(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => scheduleApi.scheduleList(params.variables),
    ...params?.options,
  })
}

