/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
import { useMutation, useQuery } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import {
  CommonErrorType,
  UserLoginErrorMessageType,
  UserPasswordResetConfirmErrorMessageType,
  UserPasswordResetErrorMessageType,
  UserRefreshErrorMessageType,
  UserRegisterErrorMessageType,
} from '../@types/data-contracts'
import {
  MutationHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { UserApi } from './User.api'

export const userApi = new UserApi({ customFetch: fetchExtended })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */

export const QUERY_KEY_USER_API = {
  LOGIN_CREATE: () => ['USER_LOGIN_CREATE'],
  PASSWORD_RESET_CREATE: () => ['USER_PASSWORD_RESET_CREATE'],
  PASSWORD_RESET_CONFIRM_CREATE: () => ['USER_PASSWORD_RESET_CONFIRM_CREATE'],
  REFRESH_CREATE: () => ['USER_REFRESH_CREATE'],
  REGISTER_CREATE: () => ['USER_REGISTER_CREATE'],
  RETRIEVE: (variables?: Parameter<typeof userApi.userRetrieve>) =>
    ['USER_RETRIEVE', variables].filter(isDefined),
  DESTROY: () => ['USER_DESTROY'],
}

/**
 * No description
 *
 * @tags user
 * @name UserLoginCreate
 * @summary 유저 로그인
 * @request POST:/v1/user/login/
 * @secure  */

export const useUserLoginCreateMutation = (
  params: MutationHookParams<
    typeof userApi.userLoginCreate,
    { error: UserLoginErrorMessageType | CommonErrorType }
  >,
) => {
  const mutationKey = QUERY_KEY_USER_API.LOGIN_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: userApi.userLoginCreate,
    ...params?.options,
  })
}

