import { InfiniteData, useInfiniteQuery, useQuery } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import { CommonErrorType } from '../@types/data-contracts'
import {
  InfiniteQueryHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { WeeklyBulletinApi } from './WeeklyBulletin.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const weeklyBulletinApi = new WeeklyBulletinApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_WEEKLY_BULLETIN_API = {
  LIST: (variables?: Parameter<typeof weeklyBulletinApi.weeklyBulletinList>) =>
    ['WEEKLY_BULLETIN_LIST', variables].filter(isDefined),
  LIST_INFINITE: (
    variables?: Parameter<typeof weeklyBulletinApi.weeklyBulletinList>,
  ) => ['WEEKLY_BULLETIN_LIST_INFINITE', variables].filter(isDefined),
  RETRIEVE: (
    variables?: Parameter<typeof weeklyBulletinApi.weeklyBulletinRetrieve>,
  ) => ['WEEKLY_BULLETIN_RETRIEVE', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags weekly_bulletin
 * @name WeeklyBulletinList
 * @summary 주보 목록 조회
 * @request GET:/v1/weekly_bulletin/
 * @secure    */

export const useWeeklyBulletinListQuery = <
  TData = RequestFnReturn<typeof weeklyBulletinApi.weeklyBulletinList>,
>(
  params?: QueryHookParams<
    typeof weeklyBulletinApi.weeklyBulletinList,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_WEEKLY_BULLETIN_API.LIST(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => weeklyBulletinApi.weeklyBulletinList(params?.variables),
    ...params?.options,
  })
}

/**
 * No description    *      * @tags weekly_bulletin
 * @name WeeklyBulletinList
 * @summary 주보 목록 조회
 * @request GET:/v1/weekly_bulletin/
 * @secure    */
export const useWeeklyBulletinListInfiniteQuery = <
  TData = InfiniteData<
    RequestFnReturn<typeof weeklyBulletinApi.weeklyBulletinList>,
    Parameter<typeof weeklyBulletinApi.weeklyBulletinList>
  >,
>(
  params?: InfiniteQueryHookParams<
    typeof weeklyBulletinApi.weeklyBulletinList,
    CommonErrorType,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_WEEKLY_BULLETIN_API.LIST_INFINITE(
    params?.variables,
  )
  return useInfiniteQuery({
    queryKey,
    initialPageParam: null,
    queryFn: ({ pageParam }) => {
      const offset = pageParam ?? params?.variables?.query?.offset ?? 0
      return weeklyBulletinApi.weeklyBulletinList({
        ...params?.variables,
        query: { ...params?.variables?.query, offset },
      })
    },
    getNextPageParam: (lastPage, allPages) => {
      if (!lastPage?.isNext) return null
      const fetchedLength = allPages?.length || 0
      const initialOffset = params?.variables?.query?.offset || 0
      const limit = params?.variables?.query?.limit || 0

      return initialOffset + fetchedLength * limit
    },
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags weekly_bulletin
 * @name WeeklyBulletinRetrieve
 * @summary 주보 상세 조회
 * @request GET:/v1/weekly_bulletin/{id}/
 * @secure    */

export const useWeeklyBulletinRetrieveQuery = <
  TData = RequestFnReturn<typeof weeklyBulletinApi.weeklyBulletinRetrieve>,
>(
  params: QueryHookParams<
    typeof weeklyBulletinApi.weeklyBulletinRetrieve,
    { error: CommonErrorType },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_WEEKLY_BULLETIN_API.RETRIEVE(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => weeklyBulletinApi.weeklyBulletinRetrieve(params.variables),
    ...params?.options,
  })
}
